import { action, Action } from "easy-peasy";

export interface StoreModel {
    name: string;
    id: string;
    setName: Action<this, string>;
}

const model: StoreModel = {
    name: "Grayson",
    id: "1",
    setName: action((state, payload) => {
        state.name = payload;
    }),
}
export default model;
