import React, { useState, useEffect, MouseEvent } from 'react';
import GenTextBox from '../../../Components/TextBox/GenTextBox';
import { races } from "./Arrays"
import { setNames, setAges, setGenders, setJobs, setTraits, setLooks, setEyeColors, setHairColors, setHairLengths, setHairTextures, setSkinColors, setSkinFeatures } from "./Functions"
import "./RandomNpc.css"

const RandomNpc = () => {

    const [race, setRace] = useState<string>("")
    const [name, setName] = useState<string>("")
    const [age, setAge] = useState<number>()
    const [gender, setGender] = useState<string>("")
    const [job, setJob] = useState<string>("")
    const [trait, setTrait] = useState<string>("")

    const [skinColor, setSkinColor] = useState<string>("")
    const [skinFeature, setSkinFeature] = useState<string>("")
    const [hairColor, setHairColor] = useState<string>("")
    const [hairTexture, setHairTexture] = useState<string>("")
    const [hairLength, setHairLength] = useState<string>("")
    const [eyeColor, setEyeColor] = useState<string>("")
    
    const [look, setLook] = useState<string[]>([])

    useEffect(() => {
        setName(setNames(race, gender))
        setAge(setAges(race))
        
    },[race]);
    useEffect(() => {
        npcGen()
    }, [])



    const npcGen = () => {
        setGender(setGenders())
        setRace(races[Math.floor(Math.random() * races.length)])
        setJob(setJobs())
        setTrait(setTraits())
        setLook(setLooks())
        setSkinColor(setSkinColors())
        setSkinFeature(setSkinFeatures())
        setHairColor(setHairColors())
        setHairTexture(setHairTextures())
        setHairLength(setHairLengths())
        setEyeColor(setEyeColors())
    }

    const onClick = (event : MouseEvent) => {
        event.preventDefault();
        const elem = event.currentTarget
        if(!(elem instanceof HTMLButtonElement)) return;

        if(elem.value === "Race"){setRace(races[Math.floor(Math.random() * races.length)])}
        else if(elem.value === "Name"){setName(setNames(race, gender))}
        else if(elem.value === "Gender"){setGender(setGenders())}
        else if(elem.value === "Job"){setJob(setJobs())}
        else if(elem.value === "Age"){setAge(setAges(race))}
        else if(elem.value === "Traits"){setTrait(setTraits())}
        else if(elem.value === "Skin Color"){setSkinColor(setSkinColors())}
        else if(elem.value === "Skin Feature"){setSkinFeature(setSkinFeatures())}
        else if(elem.value === "Hair Color"){setHairColor(setHairColors())}
        else if(elem.value === "Hair Texture"){setHairTexture(setHairTextures())}
        else if(elem.value === "Hair Length"){setHairLength(setHairLengths())}
        else if(elem.value === "Eye Color"){setEyeColor(setEyeColors())}

    }
    
    const textExport = () =>{
        navigator.clipboard.writeText(`Name: ${name} Race: ${race} Gender: ${gender} Profession: ${job} Age: ${age} Personality Trait ${trait} 
        Skin Color: ${look[0]} Skin Feature: ${look[1]} Hair Color: ${look[2]} Hair Texture: ${look[3]} Hair Length: ${look[4]} Eye Color: ${look[5]}`)
    }
    
    
    return(
        <div className ="npc">
            <div className="npcBoxContainer">
                <h1 className="npcBoxHeader">General Info</h1>
                <GenTextBox className="npcBox"payload={name}text="Name: " onClick={onClick} value="Name"/>
                <GenTextBox className="npcBox"payload={race}text="Race: " onClick={onClick} value= "Race"/>
                <GenTextBox className="npcBox"payload={gender}text="Gender: " onClick={onClick} value= "Gender"/>
                <GenTextBox className="npcBox"payload={job}text="Profession: " onClick={onClick}  value="Job"/>
                <GenTextBox className="npcBox"payload={age}text="Age: "  onClick={onClick} value="Age"/>
                <GenTextBox className="npcBox"payload={trait}text="Personality Trait: " onClick={onClick} value="Traits"/>
            </div>
            <div className="npcBoxContainer">
                <h1 className="npcBoxHeader">Appearance</h1>
                <GenTextBox className="npcBox"payload={skinColor}text="Skin Color: " onClick={onClick} value="Skin Color"/>
                <GenTextBox className="npcBox"payload={skinFeature}text="Skin Feature: " onClick={onClick} value="Skin Feature"/>
                <GenTextBox className="npcBox"payload={hairColor}text="Hair Color: " onClick={onClick} value="Hair Color"/>
                <GenTextBox className="npcBox"payload={hairTexture}text="Hair Texture: " onClick={onClick} value="Hair Texture"/>
                <GenTextBox className="npcBox"payload={hairLength}text="Hair Length: " onClick={onClick} value="Hair Length"/>
                <GenTextBox className="npcBox"payload={eyeColor}text="Eye Color: " onClick={onClick} value="Eye Color"/>
                <button onClick={ () => npcGen()}className="npcContainerButton">New NPC</button>
                <button onClick={ () => textExport()}className="npcContainerButton">Export</button>
            </div>
        </div>
    )
}
export default RandomNpc
